function solution(seq1, seq2) {
  let minV = Infinity;
  let answer = -1;

  for (let i = 0; i < seq1.length - 1; i++) {
    let j = 0;
    let cnt = 0;

    while (j < seq2.length) {
      if (seq1[i + j] !== seq2[j]) cnt++;
      j++;
    }

    if (minV > cnt) {
      minV = cnt
      answer = i;
    }
  }

  return answer + 1;
}

console.log(solution("AGATAGATAGAATGATT", "AA"));
console.log(
  solution(
    "",
    "AGATGCG"
  )
);
